package com.demos.loops;

public class incdecoperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 System.out.println("welcome\"admon\"");
	        int a=0;
	        a=++a;
	        System.out.println(a);
	        a=++a;
	        System.out.println(a);
	        a=a++;
	        System.out.println(a);
	        a=a ++;
	        System.out.println(a);
	}

}
