package com.demos.loops;

public class ForLoopDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int j=33;j>11;j--)  if(j==33) System.out.print("First for loop=="+j);else  System.out.print(","+j);
		System.out.println();
		int i;
        for(i=1;i<=10;i++) if(i==1) System.out.print("2nd for loop=="+i);else System.out.print(","+i); 
	}  
}
